        this.btnTgtId = defaultsStr(f.btn_target_id, null);

        /**
         * Button's text, defaults to Columns&#9660;
         * @type {String}
         */
        this.btnText = defaultsStr(f.btn_text, 'Columns&#9660;');

        /**
         * Button's inner HTML
